import { Component } from 'react'
import './display.css'

class Display extends Component {
    render(){

        const {number} = this.props
        return(
            <div className="display-comp">
                <input type='text' placeholder='0' dir="rtl" onChange={number}/>
            </div>
        )
    }

}

export default Display
